<?phpnamespace app\admin\controller;use app\admin\service\Work;use think\Db;use think\facade\Request;class LotteryRecordLog extends Controller{    public function index()    {        $this->checkAuth('admin:lottery_record_log:select');        $get = input();        $lotteryRecordLog = new \app\admin\service\LotteryRecordLog();        $total = $lotteryRecordLog->getTotal($get);        $page = $this->pageshow($total);        $list = $lotteryRecordLog->getList($get, $page->firstRow, $page->listRows);        $this->assign('_list', $list);        return $this->fetch();    }}