<?phpnamespace app\lottery\controller;class LotteryPrizeLog extends Controller{    public function index()    {        $this->checkAuth('lottery:lottery_prize_log:index');        $get = input();        $lotteryRecordLog = new \app\lottery\service\LotteryPrizeLog();        $total = $lotteryRecordLog->getTotal($get);        $page = $this->pageshow($total);        $list = $lotteryRecordLog->getList($get, $page->firstRow, $page->listRows);        $this->assign('_list', $list);        return $this->fetch();    }}