INSERT INTO `bx_music_singer` VALUES ('1042', 'Heath', 'http://qukufile2.qianqian.com/data2/pic/14efab8871ceb41c64e2e21789fb371e/252871407/252871407.jpg', '4', '4', '', '日本', '华语', '87913545', '小档案\r\n\r\n1968年1月22日5時22分、兵庫県尼崎市に生まれる。両親も音楽好きで家にはギター?ベース?ドラム?キーボードなどの楽器があり、家ではロックがいつも流れていた。4つ上の兄がギターをやっていたため、自然とベースを始めるようになる。兄がビートルズを弾くのに合わせてベースを弾くこともあった。生まれてはじめて買ったレコードは『嗚呼！！花の応援団』。 \r\n小学校4年生の時に母と兄に連れられて...', '40', null, '5', '1555940139');
