  `discount_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '折后价',
  `coupon_price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '优惠券面额',
  `commission_rate` varchar(10) NOT NULL DEFAULT '0' COMMENT '佣金比率',
