  `coupon_end_time` int(11) NOT NULL COMMENT '优惠券领取结束时间',
  `coupon_total` int(10) NOT NULL DEFAULT '0' COMMENT '优惠券总量',
  `coupon_surplus` int(10) NOT NULL DEFAULT '0' COMMENT '优惠券剩余量',
