  `create_time` int(11) NOT NULL DEFAULT '0' COMMENT '商品添加时间',
  `update_time` int(11) NOT NULL COMMENT '更新时间',
  `add_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '商品添加方式（0：云端自动同步；1：系统后台添加；2：用户手动添加）',
  `add_user_id` int(10) NOT NULL DEFAULT '0' COMMENT '添加商品用户id  add_type=2',
  `is_top` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否置顶',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '1：上架；0：下架',
  `collect_num` int(10) NOT NULL DEFAULT '0' COMMENT '用户收藏数量',
  `sort` int(10) NOT NULL DEFAULT '9999' COMMENT '排序',
  `is_new` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否新品（0：否；1：是）',
  `video_url` varchar(255) NOT NULL COMMENT '视频播放地址',
  `item_url` text NOT NULL COMMENT '商品链接',
  `goods_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0：一键添加；1：手动添加，可能为非淘客商品',
  `is_jhs` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否聚划算商品',
  `is_tqg` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否淘抢购',
  `is_chaoshi` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否天猫超市',
  `is_overseas` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否海外商品',
  `is_brand` tinyint(1) NOT NULL DEFAULT '0' COMMENT '大牌商品',
  `brand_id` varchar(32) NOT NULL DEFAULT '0' COMMENT '大牌id',
  `brand_name` varchar(50) NOT NULL COMMENT '大牌名称',
  `desc_score` varchar(10) NOT NULL DEFAULT '0' COMMENT '描述分',
  `desc_percent` varchar(10) NOT NULL DEFAULT '0' COMMENT '描述同行比',
  `ship_score` varchar(10) NOT NULL DEFAULT '0' COMMENT '物流分',
  `ship_percent` varchar(10) NOT NULL DEFAULT '0' COMMENT '物流同行比',
  `serv_score` varchar(10) NOT NULL DEFAULT '0' COMMENT '服务分',
  `serv_percent` varchar(10) NOT NULL DEFAULT '0' COMMENT '服务同行比',
  `is_recommand` tinyint(1) DEFAULT '0' COMMENT '是否推荐',
  `pdd_goods_sign` varchar(150) NOT NULL DEFAULT '' COMMENT '针对拼多多商品使用',
  PRIMARY KEY (`id`),
  KEY `goods_id` (`goods_id`),
  KEY `idx_cateid_shoptype_status` (`cate_id`,`shop_type`,`status`),
  KEY `idx_adduserid_addtype_status_couponendtime` (`add_user_id`,`add_type`,`status`,`coupon_end_time`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

