  `user_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '作者(当is_original为1时)记录用户id',
  `album` varchar(255) CHARACTER SET utf8mb4 DEFAULT '' COMMENT '专辑名称',
  `album_id` int(10) unsigned DEFAULT '0' COMMENT '专辑表主键',
  `singer` varchar(50) CHARACTER SET utf8mb4 NOT NULL DEFAULT '' COMMENT '歌手名',
  `singer_id` int(10) unsigned DEFAULT '0' COMMENT '歌手表主键ID',
  `languages` enum('华语','欧美','日语','韩语','粤语','其它') DEFAULT '华语' COMMENT '语种',
  `style_id` tinyint(1) unsigned DEFAULT '0' COMMENT '风格表主键id',
  `style_title` varchar(20) DEFAULT '' COMMENT '风格标题',
  `image` varchar(255) DEFAULT '' COMMENT '歌曲封面链接',
  `duration` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '时长',
  `size` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '大小',
  `all_bitrate` varchar(30) DEFAULT '' COMMENT '码率',
  `bitrate` int(10) unsigned DEFAULT '0' COMMENT '当前码率',
  `ext` varchar(10) DEFAULT 'mp3' COMMENT '格式',
  `channel_file_id` int(10) unsigned DEFAULT '0' COMMENT '第三方渠道文件id',
  `link` varchar(255) NOT NULL DEFAULT '' COMMENT '资源链接',
  `channel` enum('baidu','xiaomi','local') DEFAULT 'local' COMMENT '来源:',
  `is_original` tinyint(1) unsigned DEFAULT '0' COMMENT '是否为用户原创',
  `desc` text CHARACTER SET utf8mb4 COMMENT '相关描述',
  `use_num` int(10) unsigned DEFAULT '0' COMMENT '使用量',
  `collect_num` int(10) unsigned DEFAULT '0' COMMENT '收藏量',
  `company` varchar(255) DEFAULT '' COMMENT '所属公司',
  `create_time` int(11) unsigned DEFAULT NULL COMMENT '创作时间',
  `release_time` int(11) unsigned DEFAULT '0' COMMENT '歌曲发表时间',
  `tag` varchar(255) DEFAULT '' COMMENT '歌曲tag',
  `category_id` int(10) unsigned DEFAULT '0' COMMENT '类别表主键id',
  `lrc_link` varchar(255) DEFAULT '' COMMENT '歌词资源链接',
  `status` tinyint(1) unsigned DEFAULT '1' COMMENT '0下架',
  `lrc_report` int(10) unsigned DEFAULT '0' COMMENT '歌词举报次数',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT='乐库表';

