-- ----------------------------
-- Table structure for `bx_user`
-- ----------------------------
DROP TABLE IF EXISTS `bx_user`;
CREATE TABLE `bx_user` (
  `user_id` int(11) unsigned NOT NULL COMMENT '用户ID',
  `pid` int(11) NOT NULL DEFAULT '0' COMMENT '上架id',
  `type` enum('robot','normal') NOT NULL DEFAULT 'normal' COMMENT 'robot机器人normal正常注册 ',
  `isvirtual` enum('0','1') NOT NULL DEFAULT '0' COMMENT '是否是虚拟用户0不是1是',
  `username` varchar(30) NOT NULL DEFAULT '0' COMMENT '用户名',
  `nickname` varchar(200) NOT NULL DEFAULT '0' COMMENT '昵称',
  `remark_name` varchar(30) NOT NULL DEFAULT '' COMMENT '后台备注名称',
  `weight` smallint(4) NOT NULL DEFAULT '0' COMMENT '体重',
  `height` smallint(4) NOT NULL DEFAULT '0' COMMENT '身高',
  `gender` enum('0','1','2') NOT NULL DEFAULT '0' COMMENT '性别1男2女0保密',
  `avatar` varchar(150) NOT NULL DEFAULT '0' COMMENT '头像',
  `birthday` int(10) unsigned DEFAULT NULL COMMENT '生日时间戳',
  `province_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '所在省份ID',
  `city_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '所在城市ID',
  `district_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '所在区县ID',
  `phone_code` int(11) NOT NULL DEFAULT '86' COMMENT '手机号国家代码',
  `phone` varchar(15) NOT NULL DEFAULT '0' COMMENT '手机号',
  `first_agent_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '首次开发代理商ID',
  `status` enum('0','1') NOT NULL DEFAULT '1' COMMENT '用户状态1启用0禁用',
  `disable_time` int(11) DEFAULT NULL COMMENT '封禁时间',
  `disable_desc` varchar(300) NOT NULL DEFAULT '' COMMENT '封禁原因',
  `disable_length` varchar(30) NOT NULL DEFAULT '' COMMENT '封禁时长1 days',
  `exp` int(11) NOT NULL DEFAULT '0' COMMENT '经验值',
  `points` int(11) NOT NULL DEFAULT '0' COMMENT '用户积分',
  `level` tinyint(2) NOT NULL DEFAULT '0' COMMENT '等级',
  `live_status` enum('0','1') NOT NULL DEFAULT '0' COMMENT '开播状态0不能开播1开播',
  `film_status` enum('0','1') NOT NULL DEFAULT '1' COMMENT '短视频功能状态',
  `last_upgrade_time` int(11) DEFAULT NULL COMMENT '最近升级时间',
  `last_renick_time` int(11) DEFAULT NULL COMMENT '最近更换昵称时间',
  `verified` enum('0','1','2','3') NOT NULL DEFAULT '0' COMMENT '0未认证，1完成，2处理中，3失败',
  `is_creation` enum('0','1') NOT NULL DEFAULT '0' COMMENT '是否是创作号',
  `password` char(40) NOT NULL DEFAULT '' COMMENT '密码：sha1(密码原文+盐)',
  `salt` char(40) NOT NULL DEFAULT '' COMMENT '密码盐',
  `first_promoter_uid` int(11) NOT NULL DEFAULT '0' COMMENT '首次开发推广人ID',
  `cover` varchar(300) NOT NULL DEFAULT '' COMMENT '封面图',
  `voice_sign` varchar(255) NOT NULL DEFAULT '' COMMENT '语音标签',
  `sign` varchar(300) NOT NULL DEFAULT '这个家伙太懒了，什么也没留下。' COMMENT '个性签名',
  `reg_way` varchar(30) NOT NULL DEFAULT '0' COMMENT '注册方式',
  `login_time` int(11) unsigned DEFAULT NULL COMMENT '登录时间',
  `login_ip` varchar(15) NOT NULL DEFAULT '0' COMMENT '登录IP',
  `vip_expire` int(11) unsigned NOT NULL DEFAULT '0' COMMENT 'VIP到期时间',
  `millet` decimal(12,0) NOT NULL DEFAULT '0' COMMENT '剩余的金币',
  `total_millet` decimal(12,0) NOT NULL DEFAULT '0' COMMENT '金币总额(剩余金币+冻结金币)',
  `fre_millet` decimal(12,0) NOT NULL DEFAULT '0' COMMENT '冻结金币',
  `millet_status` enum('0','1') NOT NULL DEFAULT '1' COMMENT '1可以结算 0禁止结算',
  `his_millet` decimal(12,0) NOT NULL DEFAULT '0' COMMENT '历史金币',
  `isvirtual_millet` int(11) NOT NULL DEFAULT '0' COMMENT '收到虚拟金币（会被清空）',
  `his_isvirtual_millet` int(11) NOT NULL DEFAULT '0' COMMENT '收到虚拟金币（不会被清空）',
  `millet_cash_total` decimal(12,0) NOT NULL DEFAULT '0' COMMENT '历史总提现金币',
  `isset_pwd` enum('0','1') NOT NULL DEFAULT '0' COMMENT '是否已设置密码 0 未设置 1已设置',
  `like_num` int(11) NOT NULL DEFAULT '0' COMMENT '收到的总赞数（评论+视频）',
  `fans_num` int(11) NOT NULL DEFAULT '0' COMMENT '粉丝数量',
  `follow_num` int(11) NOT NULL DEFAULT '0' COMMENT '关注人数',
  `collection_num` int(11) NOT NULL DEFAULT '0' COMMENT '收藏数量',
  `download_num` int(11) NOT NULL DEFAULT '0' COMMENT '下载数量',
  `film_num` int(11) NOT NULL DEFAULT '0' COMMENT '发布短视频数量',
  `be_black_num` int(11) NOT NULL DEFAULT '0' COMMENT '被拉黑次数',
  `change_pwd_time` int(11) DEFAULT NULL COMMENT '最近更新密码时间',
  `is_promoter` enum('0','1') NOT NULL DEFAULT '0' COMMENT '是否是推广员0否1是',
  `is_anchor` enum('0','1') NOT NULL DEFAULT '0' COMMENT '是否是主播',
  `credit_score` int(11) NOT NULL DEFAULT '0' COMMENT '用户信用',
  `remark` varchar(300) NOT NULL DEFAULT '' COMMENT '备注',
  `reg_meid` varchar(90) NOT NULL DEFAULT '' COMMENT '注册的设备号',
  `delete_time` int(11) DEFAULT NULL COMMENT '软删除时间',
  `update_time` int(11) DEFAULT NULL COMMENT '更新资料时间',
  `create_time` int(11) unsigned NOT NULL COMMENT '创建时间',
  `processed` tinyint(1) NOT NULL DEFAULT '0',
  `like_num2` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '虚似获得点赞数',
  `pv` int(11) NOT NULL DEFAULT '0' COMMENT '浏览次数（不排重）',
  `share_num` int(11) NOT NULL DEFAULT '0' COMMENT '分享次数（不排重）',
  `comment_status` enum('0','1') NOT NULL DEFAULT '1' COMMENT '评论功能状态',
  `contact_status` enum('0','1') NOT NULL DEFAULT '1' COMMENT '私信功能状态',
  `invite_code` varchar(10) NOT NULL COMMENT '邀请码',
  `instance_id` int(11) DEFAULT '0' COMMENT '商户的id',
  `bond` double(10,2) DEFAULT '0.00' COMMENT '保证金',
  `member_level` int(10) DEFAULT '0' COMMENT 'shop小店中的会员等级',
  `balance` double(10,2) DEFAULT '0.00' COMMENT '余额',
  `score` double(10,2) DEFAULT '0.00' COMMENT '积分',
  `taoke_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '淘客余额',
  `taoke_money_status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '淘客余额提现状态，0:不可提现；1：可提现',
  `taoke_level` int(11) NOT NULL DEFAULT '0' COMMENT '淘客等级',
  `relation_id` varchar(30) NOT NULL DEFAULT '' COMMENT '淘客渠道id',
  `special_id` varchar(30) NOT NULL DEFAULT '' COMMENT '淘宝联盟会员id',
  `pdd_pid` varchar(30) NOT NULL DEFAULT '' COMMENT '拼多多pid',
  `jd_pid` varchar(30) NOT NULL DEFAULT '' COMMENT '京东pid',
  `shop_id` int(10) NOT NULL DEFAULT '0' COMMENT '店铺id',
  `taoke_shop` tinyint(1) DEFAULT '0' COMMENT '淘客小店功能功能权限（0：无；1：有)-1：申请失败/审核拒绝\n-9：未知错误，用户信息更新失败\n6：申请成功，未付款\n2：申请成功，待审核\n1：申请成功，开通成功',
  `fenxiao_level` int(11) NOT NULL DEFAULT '0',
  `cash` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '现金金额',
  `teenager_model_open` tinyint(1) NOT NULL DEFAULT '0' COMMENT '青少年模式（0：未开启；1：开启）',
  `fenxiao_id` int(11) NOT NULL DEFAULT '0' COMMENT 'shop分销商（分销有效）',
  `is_fenxiao` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'shop是否是分销商',
  `member_level_name` varchar(50) NOT NULL COMMENT 'shop会员等级名称',
  `growth` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT 'shop成长值',
  `is_auth` tinyint(1) DEFAULT '0' COMMENT 'shop是否认证',
  `order_money` decimal(10,2) DEFAULT '0.00' COMMENT 'shop付款后-消费金额',
  `order_complete_money` decimal(10,2) DEFAULT '0.00' COMMENT 'shop订单完成-消费金额',
  `order_num` int(11) DEFAULT '0' COMMENT 'shop付款后-消费次数',
  `order_complete_num` int(11) DEFAULT '0' COMMENT 'shop完成的订单数量',
  `balance_withdraw` decimal(10,2) DEFAULT '0.00' COMMENT 'shop已提现余额',
  `balance_money` decimal(10,2) DEFAULT '0.00' COMMENT 'shop现金余额',
  `point` decimal(10,2) DEFAULT '0.00' COMMENT 'shop积分',
  `balance_withdraw_apply` decimal(10,2) DEFAULT '0.00' COMMENT '提交申请余额',
  `live_money` decimal(10,2) DEFAULT '0.00' COMMENT '商城直播带货佣金',
  `pay_password` varchar(40) DEFAULT NULL COMMENT '商城余额支付密码',
  `live_withdraw_apply` decimal(10,2) DEFAULT '0.00' COMMENT '提交申请直播d带货佣金',
  `voice_time` int(11) DEFAULT '0',
  PRIMARY KEY (`user_id`),
  KEY `user_id` (`user_id`,`delete_time`),
  KEY `phone` (`phone`,`delete_time`),
  KEY `phone_code` (`phone_code`,`phone`,`delete_time`),
  KEY `invite_code` (`invite_code`),
  KEY `idx_firstagentid_isvirtual` (`first_agent_id`,`isvirtual`),
  KEY `nickname` (`nickname`(191))
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

