  `height` int(11) NOT NULL DEFAULT '0' COMMENT '视频高度',
  `file_size` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '文件大小字节',
  `copy_right` tinyint(1) unsigned DEFAULT '0' COMMENT '版权：0无标识 1有标识',
  `rating` int(11) NOT NULL DEFAULT '0' COMMENT '人工评分（满分100）',
  `score` int(11) NOT NULL DEFAULT '0' COMMENT '总评分',
