  `location_id` int(11) NOT NULL DEFAULT '0' COMMENT '位置ID',
  `region_level` tinyint(1) unsigned DEFAULT '4' COMMENT '区域级别：0国家、1省、2市、3区县、4街道',
  `music_id` int(11) NOT NULL DEFAULT '0' COMMENT '音乐ID',
  `visible` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0公开，1互关好友可见，2私密 ',
  `ai_cover` varchar(300) NOT NULL DEFAULT '' COMMENT '智能识别封面',
