  `create_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '发布时间',
  `zan_sum2` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '虚拟获赞量',
  `play_sum2` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '虚拟播放数量',
  `sco_comment_sum` int(11) NOT NULL DEFAULT '0' COMMENT '评论数量（用于评分）',
  `switch_sum` int(11) NOT NULL DEFAULT '0' COMMENT '切换次数',
  `played_out_sum` int(11) NOT NULL DEFAULT '0' COMMENT '完播次数',
  `general_sum` int(11) NOT NULL DEFAULT '0' COMMENT '正常播放次数',
  `sco_share_sum` int(11) NOT NULL DEFAULT '0',
  `sco_down_sum` int(11) NOT NULL DEFAULT '0',
  `sco_zan_sum` int(11) NOT NULL DEFAULT '0',
