  `goods_type` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '0表示第三方商品 1表示自营',
  `cate_id` int(10) NOT NULL DEFAULT '0' COMMENT '商品id',
  `weight` int(11) DEFAULT '0' COMMENT '权重',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

